#!/usr/bin/env python3
# _*_ coding: utf-8 _*_

import base64, struct
bmp_data = base64.b64decode('')
def bmp_info(data):
    data_unpack=struct.unpack('<ccIIIIIIHH',data[0:30])
    if data_unpack[0]==b'B' and data_unpack[1]==b'M':
        return {
            'width': data_unpack[-4],
            'height':data_unpack[-3],
            'color': data_unpack[-1]
        }
# 测试
bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')
